import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends Component {

  constructor() {
    super()
    const url = `https://api.openweathermap.org/data/2.5/weather?q=London&unit=imperial&appid=381e045e4c2c96594b3f565f73e49cbb`
    axios.get(url).then((resp)=>{
      this.state = {
        temp: resp.data.main.temo
      }
    })
  }
  render() {
  return (
      <div className="App">
        <h1> {this.state.temp}</h1>
      </div>
    );
  }
}
export default App;
